function Image_display (){
    fetch('http://localhost:3001/Image_Url')
        .then(response => response.blob()) // Get the image data as a Blob
        .then(blob => {
            const imageURL = URL.createObjectURL(blob);

            if (!existingImage) {
                const imgElement = document.createElement('img');
                imgElement.src = imageURL;
                // imgElement.style.position = 'absolute'; // Set position to absolute
                // imgElement.style.top = '50px'; // Set the top position in pixels
                // imgElement.style.right = '100px'; // Set the left position in pixels
                imgElement.style.width = '200px'; // Set the width in pixels or other CSS units
                imgElement.style.height = 'auto'; // Set the height to 'auto' to maintain aspect ratio
                document.body.appendChild(imgElement);
            }
        })
        .catch(error => {
            console.error(error);
        });

}
export default Image_display;
