import React, {useState} from "react"; 
import Maze from './Maze';

function App() {
  const [mazeMatrix, updateMap] = useState([[]]);

  React.useEffect(() => { ///See CORS
    fetch("http://localhost:3001/mazeMatrix/") 
        .then((res) => res.json())
        .then((data) => updateMap(data.mazeMatrix)) 
        .catch((err) => alert(err)
    );
  }, [updateMap]);

  const handleClick = (updateMethod) => { 
    fetch("http://localhost:3001/mazeMatrix/")
      .then((res) => res.json())
      .then((data) => updateMethod(data.mazeMatrix)) 
      .catch((err) => alert(err)
    ); 
  }
  return (
    <div className="App">
      <h1 style={{ fontSize: '36px' }}>EBB-Imperial   Maze</h1>
      <Maze td = {mazeMatrix}/>
      <button onClick={() => handleClick(updateMap)}>Randomize Maze</button>
    </div> );

}
export default App;



