function Image_display (props){
    // Create the image element
    const imageElement = document.createElement('png');
    imageElement.src = 'client2/src/image/test_img.png'; // Replace with the actual path to your image

    // Add CSS styles to position the image
    imageElement.style.position = 'fixed';
    imageElement.style.top = '20px';
    imageElement.style.right = '20px';
    imageElement.style.width = '200px'; // Adjust the desired width of the image
    imageElement.style.height = 'auto'; // Maintain the aspect ratio of the image

    // Add the image element to the picture container
    const pictureContainer = document.getElementById('pictureContainer');
    pictureContainer.appendChild(imageElement);

    return imageElement
}
export default Image_display;
